import React, { Component } from 'react'
import {Card,Form,Input,Cascader,Upload,Button} from 'antd'
import LinkButton from '../../component/link-button'
import { RollbackOutlined } from "@ant-design/icons";
const Item = Form.Item
const TextArea = Input.TextArea
export default class AddUpdate extends Component {
    render() {
        const title=(<span><LinkButton><RollbackOutlined/></LinkButton>添加商品</span>)
        const formItemLayout = {
            labelCol:{span:3},//左侧label宽度
            wrapperCol:{span:8}//右侧包裹输入框宽度
        }
        return (
            <Card title={title}>
                <Form {...formItemLayout}  >
                    <Item label='商品名称'><Input placeholder='商品名称' ></Input></Item>
                    <Item label='商品描述'><TextArea placeholder='商品描述' autoSize={{minRows:2,maxRows:6}}></TextArea></Item>
                    <Item label='商品价格'><Input type='number' placeholder='商品价格' addonAfter="元"></Input></Item>
                    {/* 输入的是数值,指定type */}

                </Form>
            </Card>
        )
    }
}
