import React, { Component } from 'react'
import {Card,Table,Button} from 'antd'
import {PlusOutlined}from '@ant-design/icons'
import LinkButton from '../../component/link-button'
import {reqCategorys,reqUpdateCategory,reqAddCategory} from '../../api/index'
export default class Category extends Component {
    state = {
        categorys:[],
    }
    /*
    初始化Table所有列的数组
    */initTable=()=>{
        const dataSource = [
            {
              key: '1',
              name: '胡彦斌',
              age: 32,
              address: '西湖区湖底公园1号',
            },
          ];
          
          const columns = [
            {
              title: '分类名称',
              dataIndex: 'name',
              key: 'name',
            },
            {
              title: '操作',
              width:300,
              key: 'action',
              dataIndex: '',
              render: () => <span><LinkButton>修改分类</LinkButton>   <LinkButton>查看子分类</LinkButton></span>,
            },
          ];
    }
    componentDidMount=()=>{
        // reqCategorys,reqUpdateCategory,reqAddCategory,
        this.initTable()
    }
    render() {
        //card左侧
        

        const title = '一级分类列表'
        //card右侧
        const extra = (
            <Button icon={<PlusOutlined />} type='primary'>
                添加
            </Button>
        )
        return (
                <Card title={title} extra={extra} >
                <Table rowKey='_id' pagination={{ pageSize: 4,total:50}} dataSource={dataSource} columns={columns} />
                </Card>
        )
    }
}
