import React, { Component } from 'react' 
import {Card,Button,Table} from 'antd'
/* 角色路由 */
export default class Role extends Component {
    state = {roles:[]}
    initColumn = ()=>{
        this.columns=[
            {
            title:'角色名称',
            dateIndex:'name',},
            {
            title:'创建时间',
            dateIndex:'name',},
            {
            title:'授权时间',
            dateIndex:'name',},
            {
            title:'授权人',
            dateIndex:'name',},
        ]
    }
    render() {
        const title =(<span>
            <Button type='primary'>创建角色</Button>
            <Button type='primary' disabled>设置角色权限</Button>
        </span>)
        const {roles} = this.state
        
        return (
            <Card title={title}>
                <Table rowKey="_id"
          pagination={{ pageSize: 5
            // , total: 50 
          }}
          dataSource={roles}
          columns={this.columns}
          loading={this.state.loading}></Table>
            </Card>
        )
    }
}
