import React, { Component } from "react";
import { Button } from "antd";
import ReactEcharts from "echarts-for-react";

import './index.less'
export default class Bar extends Component {
    state={
        sales:[5,20,36,10,10,20],
        stores:[6,10,25,20,15,10],
    }
  /* 返回柱状图的配置对象 */
  getOption = () => {
    return {
      title: {
        text: "销量",
      },
      tooltip: {
        trigger: "axis",
        axisPointer: {
          // 坐标轴指示器，坐标轴触发有效
          type: "shadow", // 默认为直线，可选为：'line' | 'shadow'
        },
      },
      grid: {
        left: "3%",
        right: "4%",
        bottom: "3%",
        containLabel: true,
      },
      legend: {
        data: ["销量", "库存"],
      },
      xAxis: [
        {
          type: "category",
          data: ["衬衫", "羊毛衫", "裤子", "雪纺衫", "高跟鞋", "袜子", "球鞋"],
          axisTick: {
            alignWithLabel: true,
          },
        },
      ],
      yAxis: [
        {
          type: "value",
        },
      ],
      series: [
        {
          name: "销量",
          type: "bar",
          // barWidth: '60%',
          data: [50, 32, 20, 334, 90, 33, 20],
        },
        {
          name: "库存",
          type: "bar",
          // barWidth: '60%',
          data: [10, 52, 200, 334, 390, 330, 220],
        },
      ],
    };
  };
  render() {
      const {sales,stores} = this.state
    return (
      <div className='bar'>
        <Button type="primary">更新</Button>
          <hr></hr>
          <h1
            style={{  fontSize: "20px", fontWeight: "bold" }}
          >
            柱状图
          </h1>
          <hr></hr>
          <ReactEcharts option={this.getOption()}></ReactEcharts>
      </div>
    );
  }
}
