import React, { Component } from "react";
import { Card, Form, Input, Cascader, Upload, Button } from "antd";
import LinkButton from "../../component/link-button";
import { RollbackOutlined } from "@ant-design/icons";
import { options } from "less";
import {reqCategorys} from '../../api'
const Item = Form.Item;
const TextArea = Input.TextArea;
export default class AddUpdate extends Component {
    state={
        options:[
          ],
        setOptions:()=>{},
    };
    componentDidMount=()=>{
        this.getCategory()
    }
    getCategory = async (parentId) =>{
        const result = await reqCategorys(parentId)
        if (result.data.status===0){
            const categorys= result.data.data
        }
        this.setState({option:categorys})
    }
    onChange = (value, selectedOptions) => {
        console.log(value, selectedOptions);
      };
      /* 用来加载下面数字组 */
      loadData = (selectedOptions) => {
        const targetOption = selectedOptions[selectedOptions.length - 1];
        //显示loading
        targetOption.loading = true;
      //   load options lazily
      //模拟异步请求获取二级列表数据
        setTimeout(() => {
          targetOption.loading = false;
          targetOption.children = [
            {
              label: `${targetOption.label} Dynamic 1`,
              value: "dynamic1",
              isLeaf: true,
            },
            {
              label: `${targetOption.label} Dynamic 2`,
              value: "dynamic2",
              isLeaf: true,
            },
          ];
          this.setState({options:[...this.state.options]});
        }, 100);
      }
  render() {
    

    
      
    const title = (
      <span>
        <LinkButton>
          <RollbackOutlined />
        </LinkButton>
        添加商品
      </span>
    );
    const formItemLayout = {
      labelCol: { span: 3 }, //左侧label宽度
      wrapperCol: { span: 8 }, //右侧包裹输入框宽度
    };
    const onFinish = (values: any) => {
      console.log("Success:", values);
    };
    const tailLayout = {
      wrapperCol: { offset: 8, span: 16 },
    };
    const onFinishFailed = (errorInfo: any) => {
      console.log("Failed:", errorInfo);
    };

    // function onChange(value) {
    //   console.log(value);
    // }
    return (
      <Card title={title}>
        <Form
          {...formItemLayout}
          onFinish={onFinish}
          onFinishFailed={onFinishFailed}
        >
          <Item
            name="productName"
            label="商品名称"
            rules={[{ required: true, message: "必须输入商品名称!" }]}
          >
            <Input placeholder="请输入商品名称"></Input>
          </Item>
          <Item name="productDes" label="商品描述">
            <TextArea
              placeholder="请输入商品描述"
              autoSize={{ minRows: 2, maxRows: 6 }}
            ></TextArea>
          </Item>
          <Item
            name="productPrice"
            label="商品价格"
            rules={[
              { required: true, message: "必须输入商品价格!" },
              {
                validator: (_, value) =>
                  !value || value * 1 > 0
                    ? Promise.resolve()
                    : Promise.reject(new Error("商品价格必须大于0")),
              },
            ]}
          >
            <Input
              type="number"
              placeholder="请输入商品价格"
              addonAfter="元"
            ></Input>
          </Item>
          <Item
            name="productClass"
            label="商品类别"
            rules={[{ required: true, message: "必须选择商品类别!" }]}
          >
            <Cascader
              placeholder="请选择"
              options={this.state.options}
              loadData={this.loadData}
              onChange={this.onChange}
              changeOnSelect
            ></Cascader>
          </Item>
          {/* 输入的是数值,指定type */}
          <Form.Item {...tailLayout}>
            <Button type="primary" htmlType="submit">
              Submit
            </Button>
          </Form.Item>
        </Form>
      </Card>
    );
  }
}
