import React, { Component } from 'react' 
import {Card,Button,Table} from 'antd'
import {PAGE_SIZE} from '../../utils/constant'
/* 角色路由 */
export default class Role extends Component {
    state = {roles:[{
        "menus": [
          "/products",
          "/product",
          "/charts/line",
          "/charts/bar",
          "/category",
          "/user"
        ],
        "_id": "5ff5ad2aa4aca2121c004678",
        "name": "游客3",
        "create_time": 1609936170648,
        "__v": 0,
        "auth_time": 1613985379434,
        "auth_name": "admin"
      },
      {
        "menus": [
          "/home",
          "/products",
          "/category",
          "/product",
          "/role"
        ],
        "_id": "5ff5ad9ca4aca2121c004679",
        "name": "游客4",
        "create_time": 1609936284070,
        "__v": 0,
        "auth_time": 1614433024355,
        "auth_name": "test001"
      }]}
    initColumn = ()=>{
        this.columns=[
            {
            title:'角色名称',
            dateIndex:'name',},
            {
            title:'创建时间',
            dateIndex:'create_time',},
            {
            title:'授权时间',
            dateIndex:'auth_time',},
            {
            title:'授权人',
            dateIndex:'admin',},
        ]
    }
    render() {
        const title =(<span>
            <Button type='primary'>创建角色</Button>
            <Button type='primary' disabled>设置角色权限</Button>
        </span>)
        const {roles} = this.state
        
        return (
            <Card title={title}>
                <Table rowKey="_id"
          pagination={{ deafaultPageSize: PAGE_SIZE
            // , total: 50 
          }}
          dataSource={roles}
          columns={this.columns}
          loading={this.state.loading}
          rowSelection={{type:'radio'}}></Table>
            </Card>
        )
    }
}
