import React, { Component } from 'react'
import {Card,Table,Button} from 'antd'
import {PlusOutlined}from '@ant-design/icons'
import LinkButton from '../../component/link-button'
import {reqCategorys,reqUpdateCategory,reqAddCategory} from '../../api/index'
export default class Category extends Component {
    state = {
        categorys:[],
    }
    /*
    初始化Table所有列的数组
    */initTable=()=>{
        this.dataSource = this.state.categorys
          
          this.columns = [
            {
              title: '分类名称',
              dataIndex: 'name',
              key: 'name',
            },
            {
              title: '操作',
              width:300,
              key: 'action',
              dataIndex: '',
              render: () => <span><LinkButton>修改分类</LinkButton>   <LinkButton>查看子分类</LinkButton></span>,
            },
          ];
    }
    /* 異物获取一级分类列表显示 */
    getCategorys = async()=>{
        const result = await reqCategorys('0')
        if(result.status===0){
            const categorys = result.data
            this.setState({categorys})
        }
    }
    /* 准备数据 */
    componentDidMount(){
        // reqCategorys,reqUpdateCategory,reqAddCategory,
        this.initTable()
    }
    /* 执行异步任务 */
    componentDidMount(
       
    ){

    }
    render() {
        //card左侧
        

        const title = '一级分类列表'
        //card右侧
        const extra = (
            <Button icon={<PlusOutlined />} type='primary'>
                添加
            </Button>
        )
        return (
                <Card title={title} extra={extra} >
                <Table rowKey='_id' pagination={{ pageSize: 4,total:50}} dataSource={this.dataSource} columns={this.columns} />
                </Card>
        )
    }
}
