import React, { Component } from 'react'
import {Card,Select,Input,Button,Table}from 'antd'
import {} from '@ant-design/icons'
import LinkButton from '../../component/link-button'
/* 默认字路由 */
const Option = Select.Option
export default class Home extends Component {
    UNSAFE_componentWillMount(){
            this.columns = [
                {
                  title: '商品名称',
                  dataIndex: 'name',
                  key: 'name',
                },
                {
                  title: '商品描述',
                  dataIndex: 'desc',
                  key: 'desc',
                },
                {
                  title: '价格',
                  dataIndex: 'price',
                  key: 'price',
                  render:(price) => '￥'+price
                  
                },
                {
                  title: '状态',
                  dataIndex: 'status',
                  key: 'status',
                  render:(status) => {
                      return status==='1'?<span>
                      <Button>下架</Button>
                      <span>在售</span></span>:<span>
                      <Button>上架</Button>
                      <span>下架</span></span>
                  }
                },
                {
                  title: '操作',
                  dataIndex: 'action',
                  key: 'action',
                  render:()=>{
                    <LinkButton>修改</LinkButton>;
                    <LinkButton>下架</LinkButton>
                  }
                },
              ];    
    }
    render() {
        const title=(
            <span><Select style={{width:150}}><Option value='1' >按名称搜索</Option></Select><Input style={{width:200}} placeholder='关键字'/><Button type='primary'>搜索</Button></span>)
        const extra=(
            <button></button>)
            const dataSource = [
                {
                    "status": 1,
                    "imgs": [
                      "image-1614417214360.png"
                    ],
                    "_id": "5fe500e4cc325b1aceb361f1",
                    "name": "91a",
                    "desc": "800000",
                    "price": 70,
                    "detail": "<p>888</p>\n<img src=\"http://120.55.193.14:5000/upload/image-1613720173579.jpg\" alt=\"1\" style=\"height: auto;width: auto\"/>\n<p></p>\n<p></p>\n<img src=\"http://120.55.193.14:5000/upload/image-1612755387489.jpg\" alt=\"ni\" style=\"height: auto;width: auto\"/>\n<p>aaaaaaaaaaaaaaaaaaaaaaaa</p>\n",
                    "pCategoryId": "5fea8d7ccc325b1aceb361fb",
                    "categoryId": "60224b5634c1ad21f1c45ff4",
                    "__v": 0
                  },
                  {
                    "status": 1,
                    "imgs": [
                      "image-1612600844941.jpg"
                    ],
                    "_id": "5fe640cacc325b1aceb361f3",
                    "name": "商品管理",
                    "desc": "852269",
                    "price": 2e+61,
                    "detail": "<p><span style=\"color: rgb(184,49,47);font-size: 96px;\">你快乐吗<del><strong><em>fjfffhfh200</em></strong></del></span><img src=\"http://localhost:5000/upload/image-1613741930890.jpg\" alt=\"11\" style=\"height: auto;width: auto\"/></p>\n<p></p>\n",
                    "pCategoryId": "5fea8d7ccc325b1aceb361fb",
                    "categoryId": "602680b034c1ad21f1c4603a",
                    "__v": 0
                  }
              ];
              
              
      
        return (
            
            <Card title={title} extra={extra}>
                <Table  rowKey='_id' dataSource={dataSource} columns={this.columns} />;
            </Card>
        )
    }
}
