import React, { Component } from "react";
import { Card, List } from "antd";
import { RollbackOutlined } from "@ant-design/icons";
import { withRouter } from 'react-router-dom'

 class Detail extends Component {
  render() {
      //读取携带过来的state
    const {name,desc,price,detail,imgs}= this.props.location.state.desc
    console.log(name,desc,price,detail,imgs)
    // console.log(this.props)
    const title = (
      <span>
        <RollbackOutlined onClick={()=>this.props.history.goBack()}/> 商品详情
      </span>
    );
    return <Card title={title}>
        <List>
            <List.Item >
                <span className='left'>商品名称:</span><span >{name}</span>
            </List.Item>
            <List.Item>
                <span className='left'>商品描述:</span>
                <span >{desc}</span>
            </List.Item>
            <List.Item>
                <span className='left'>商品价格:</span>
                <span >{price}</span>
            </List.Item>
            <List.Item>
                <span className='left'>所属分类:</span>
                联想thinkpad
            </List.Item>
            <List.Item>
                <span className='left'>商品图片:</span>
                <img  className="product-img" src='http://120.55.193.14:5000/upload/image-1613741930890.jpg' alt='img'/>
                <img></img>
            </List.Item>
            <List.Item style={{float:'left'}}>
                <span className='left'>商品详情:</span>
                <span dangerouslySetInnerHTML={{__html:detail}}></span>
            </List.Item>
        </List>
    </Card>;
  }
}
export default withRouter(Detail);
