import React, { Component } from 'react'
import './index.css'

//创建context对象
const UserNameContext = React.createContext()
export default class A extends Component {
    state={username:'tom'}
    render() {
        return (
            <div className='parent'>
                <h3>我是A组件</h3>
                <h4>我的用户名是:{this.state.username}</h4>
                <MyContext.Provider>
                <B />
                </MyContext.Provider>

            </div>
        )
    }
}

 class B extends Component {
    render() {
        return (
            <div className='child'>
                <h3>我是B组件</h3>
                <h4>我的从A收到的用户名是:{this.props.username}</h4>
                <C username={this.props.username}/>
            </div>
        )
    }
}

 class C extends Component {
    render() {
        return (
            <div className='grand'>
                <h3>我是C组件</h3>
                <h4>我从A收到的用户名是:{this.props.username}</h4>
            </div>
        )
    }
}
