import React, { PureComponent } from "react";
import { Form, Input, Select } from "antd";
import PropTypes from 'prop-types'

const Item = Form.Item;

export default class UserForm extends PureComponent {
  static propTypes = {
    roles: PropTypes.array.isRequired,
    // setForm: PropTypes.func.isRequired,
  };

  render() {
      
    const formItemLayout = {
        labelCol: { span: 4 }, //左侧label宽度
        wrapperCol: { span: 10 }, //右侧包裹输入框宽度
      };
    const { roles } = this.props;
    return (
      <Form  {...formItemLayout}>
        <Item
        label='用户名'
          name="username"
          rules={[{ required: true, message: "用户名必须输入!" }]}
        >
          <Input
            placeholder="请输入用户名"
          ></Input>
        </Item>
        <Item
        label='密码'
          name="password"
          rules={[{ required: true, message: "密码必须输入!" }]}
        >
          <Input
          type='password'
            placeholder="请输入密码"
          ></Input>
        </Item>
        <Item
        label='手机号'
          name="phone"
          rules={[{ required: true, message: "手机号必须输入!" }]}
        >
          <Input
        //   type='password'
            placeholder="请输入手机号"
          ></Input>
        </Item>
        <Item
        label='邮箱'
          name="email"
          rules={[{ required: true, message: "邮箱必须输入!" }]}
        >
          <Input
        //   type='password'
            placeholder="请输入邮箱"
          ></Input>
        </Item>
        <Item
        label='角色'
          name="role"
          rules={[{ required: true, message: "邮箱必须输入!" }]}
        >
          <Select>
                {
                    roles.map(role=><Select.Option key={role._id}value={role._id}>{role.name}</Select.Option>)
                }
              
          </Select>
        </Item>

      </Form>
    );
  }
}
