import React, { Component } from "react";
import { Card, Table, Button, message } from "antd";
import { PlusOutlined,ArrowRightOutlined  } from "@ant-design/icons";
import LinkButton from "../../component/link-button";
import {
  reqCategorys,
  reqUpdateCategory,
  reqAddCategory,
} from "../../api/index";
export default class Category extends Component {
  state = {
    categorys: [],
    subCategorys:[],//二级分类列表
    loading:false,
    parentId:'0',//当前需要显示的分类ID
    parentName:'',//当前需要显示的分类名称

  };
  /*
  /* 異物获取一级或者耳机分类列表显示 */
  getCategorys = async () => {
      this.setState({loading:true})
      const {parentId} = this.state
    const result = await reqCategorys(parentId);
    this.setState({loading:false})
    if (result.data.status === 0) {

      //去除分类数组
      const categorys = result.data.data;
      if(parentId==='0'){
        this.setState({ categorys });
      }else{
        this.setState({subCategorys:categorys})
      }
      // console.log(categorys)
      
    }else{
      message.error('获取分类列表失败')
    }
  };
  /* 展现指定对象的子列表 */
  showSubCategory= (category)=>{
    //更新状态时异步的
    this.setState({
      parentId:category._id,
      parentName:category.name,
    },()=>{//在状态更新后重新render后执行
      this.getCategorys()
      console.log(this.state.parentId)
      console.log(this.state.subCategorys)

    })
// 获取二级分类列表显示
console.log(this.state.parentId)
  }
  /* 准备数据 */
  componentWillMount() {
    // reqCategorys,reqUpdateCategory,reqAddCategory,
    this.columns = [
        {
          title: "分类名称",
          dataIndex: "name",
          key: "name",
        },
        {
          title: "操作",
          width: 300,
          key: "action",
          dataIndex: "",
          render: (category) => (
            <span>
              <LinkButton onClick={this.add}>修改分类</LinkButton>{" "}
              {/* 如何向事件回调函数传递参数,定义一个回调函数 */}
              {this.state.parentId==='0'?<LinkButton onClick={()=>{this.showSubCategory(category)}}>查看子分类</LinkButton>:null}
              
            </span>
          ),
        },
      ];
  }
  /* 执行异步任务 */
  componentDidMount() {
    this.getCategorys();
  }
  render() {
    //card左侧

    
    //card右侧
    const extra = (
      <Button icon={<PlusOutlined />} type="primary">
        添加
      </Button>
    );
    const {parentId,categorys,subCategorys} = this.state
    const title = parentId ==='0' ? '一级分类列表' : (
      <span><LinkButton>一级分类列表</LinkButton><ArrowRightOutlined />{subCategorys.name}</span>
      );
    // {console.log(this.columns)}
    return (
      <Card title={title} extra={extra}>
        <Table
          rowKey="_id"
          pagination={{ pageSize: 5, total: 50 }}
          dataSource={parentId==='0'?categorys:subCategorys}
          columns={this.columns}
          loading={this.state.loading}
        />
      </Card>
    );
  }
}
