import React, { Component } from "react";
import { Card, Form, Input, Cascader, Upload, Button } from "antd";
import LinkButton from "../../component/link-button";
import { RollbackOutlined } from "@ant-design/icons";
const Item = Form.Item;
const TextArea = Input.TextArea;
export default class AddUpdate extends Component {
  render() {
    const title = (
      <span>
        <LinkButton>
          <RollbackOutlined />
        </LinkButton>
        添加商品
      </span>
    );
    const formItemLayout = {
      labelCol: { span: 3 }, //左侧label宽度
      wrapperCol: { span: 8 }, //右侧包裹输入框宽度
    };
    const onFinish = (values: any) => {
      console.log("Success:", values);
    };
    const tailLayout = {
      wrapperCol: { offset: 8, span: 16 },
    };
    const onFinishFailed = (errorInfo: any) => {
      console.log("Failed:", errorInfo);
    };
    const options = [
        {
          value: 'zhejiang',
          label: 'Zhejiang',
          children: [
            {
              value: 'hangzhou',
              label: 'Hangzhou',
              children: [
                {
                  value: 'xihu',
                  label: 'West Lake',
                },
              ],
            },
          ],
        },
        {
          value: 'jiangsu',
          label: 'Jiangsu',
          children: [
            {
              value: 'nanjing',
              label: 'Nanjing',
              children: [
                {
                  value: 'zhonghuamen',
                  label: 'Zhong Hua Men',
                },
              ],
            },
          ],
        },
      ];
      
      function onChange(value) {
        console.log(value);
      }
    return (
      <Card title={title}>
        <Form
          {...formItemLayout}
          onFinish={onFinish}
          onFinishFailed={onFinishFailed}
        >
          <Item
            name="productName"
            label="商品名称"
            rules={[{ required: true, message: "必须输入商品名称!" }]}
          >
            <Input placeholder="请输入商品名称"></Input>
          </Item>
          <Item name="productDes" label="商品描述">
            <TextArea
              placeholder="请输入商品描述"
              autoSize={{ minRows: 2, maxRows: 6 }}
            ></TextArea>
          </Item>
          <Item
            name="productPrice"
            label="商品价格"
            rules={[
              { required: true, message: "必须输入商品价格!" },
              {
                validator: (_, value) =>
                  !value||value*1>0 ? Promise.resolve() : Promise.reject(new Error('商品价格必须大于0')),
              },
            ]}
          >
            <Input
              type="number"
              placeholder="请输入商品价格"
              addonAfter="元"
            ></Input>
          </Item>
          <Item
            name="productClass"
            label="商品类别"
            rules={[{ required: true, message: "必须选择商品类别!" }]}
          >
            <Cascader defaultValue={['zhejiang', 'hangzhou', 'xihu']}
    options={options}
    onChange={onChange}></Cascader>
          </Item>
          {/* 输入的是数值,指定type */}
          <Form.Item {...tailLayout}>
            <Button type="primary" htmlType="submit">
              Submit
            </Button>
          </Form.Item>
        </Form>
      </Card>
    );
  }
}
